#!/usr/bin/env python3

import base64
import os

# Create simple placeholder images for each file type
placeholders = {
    'image': """
    
    """,
    
    'video': """
    
    """,
    
    'audio': """
    
    """,
    
    'document': """
    
    """
}

# Convert base64 data to image files
for file_type, base64_data in placeholders.items():
    # Clean the base64 data
    base64_data = base64_data.strip()
    
    # Decode the base64 data
    image_data = base64.b64decode(base64_data)
    
    # Write to file
    with open(os.path.join(os.path.dirname(__file__), f"{file_type}.png"), 'wb') as f:
        f.write(image_data)

print("Placeholder images created successfully.")